{% block title %}Hello BlogController!{% endblock %}

{% block body %}

<section class=\"articles\">

    {% for article in articles %}

    <article>
        <h2>{{article.title}}</h2>
        <div class=\"metadata\">Ecrit le {{article.createdAt | date('d/m/Y')}} à {{article.createdAt | date('H:i')}} dans la catégorie {{ article.category.title }} </div>
        <div class=\"content\">
            <img src=\"{{article.image}}\" alt=\"\" >
            <p>{{article.content | raw}}</p>
            <a href=\"{{ path('blog_show', {'id' : article.id}) }}\" class=\"btn btn-danger\">Lire la suite</a>
            <a href=\"{{ path('blog_edit', {'id': article.id}) }}\" class=\"btn btn-primary\">Modifier l'article</a>
        </div>
    </article>

    {% endfor %}
   
</section>

{% endblock %}
